mapper.select:

SELECT * FROM (
    SELECT ROWNUM rn, a.*
    FROM (
        SELECT * FROM tb_qna ORDER BY created_at DESC
    ) a
    WHERE ROWNUM <= #{endRow}
)
WHERE rn >= #{startRow}

mapper.insert:
insert into tb_qna(qna_title, qna_content, qna_file, created_at,
qna_views, id) values(
#{qna_title}, #{qna_content}, #{qna_file}, systimestamp, #{qna_views}, #{id}
)
